//Dependencies//
import React from "react";
import { client, w3cwebsocket as W3CWebSocket } from "websocket";
import { useEffect, useState, useRef } from "react";
import { Card, Avatar, Input, Typography } from 'antd';

//Components//
import Home from "./Home"
import Login from "./Login"

const { Search } = Input;
//Define socket
const socketClient = new W3CWebSocket("ws://localhost:8000/");
export default function App() {
    //Dont' let anything happen until websocket connection is made
    let [isOpen, setIsOpen] = useState(false);
    //Authentication State
    let [userState, setUserState] = useState({
        "userName": '',
        "isLoggedIn": false
    })



useEffect(() => {
    //Open up connection
    socketClient.onopen = () => {
        console.log("Websocket Client Connected")
        setIsOpen(true)
    }

    socketClient.onmessage = (message) => {
        const dataFromServer = JSON.parse(message.data);
        console.log('Reply from Server: ', dataFromServer)
    }
})


//   Pushes messaging information
function onButtonClicked(value) {
    if(isOpen){
        console.log("You clicked a button")
        socketClient.send(JSON.stringify({
            type:"message",
            msg: value
        }))
    
    }}

//<--- Conditionally Render Components--->//
let loginComponent;
userState.isLoggedIn === false ? 
    // loginComponent = <Login /> 
    loginComponent = (
        <div>
            <Search
            placeholder = "Enter Username"
            enterButton= "Go Forth"
            size="large"
            onSearch = {value => setUserState({"userName": value, isLoggedIn: true})} />
        </div>
    )
    :
    loginComponent = <Home />
  return (
    <div>
      <h4>Home</h4>
        {loginComponent}
    </div>
  );
}
