//Dependencies
import React from 'react'
import { client, w3cwebsocket as W3CWebSocket } from "websocket";

//Some bullshit from Ant Designs
import { Card, Avatar, Input, Typography } from "antd";
import "antd/dist/antd.css";
import "./index.css";

const { Search } = Input;
const { Text } = Typography;
const { Meta } = Card;

//Web Socket Connection Info
const socketClient = new W3CWebSocket("ws://localhost:8000/");
export default function Home(props) {
    
//   Pushes messaging information
function onButtonClicked(value) {
    if(props.isSocketOpen){
        console.log("You clicked a button")
        socketClient.send(JSON.stringify({
            type:"message",
            msg: value,
            user: props.userName
        }))
    }}

//  Chat Div Builder
    let chatDiv;
    if(props.chatHistory !== []){
        chatDiv = (
            props.chatHistory.map(msg => 
                <div>
                    <p>{msg.user}</p>
                    <p>{msg.msg}</p>
                </div> )
        )

    } else {
        chatDiv = (
            <div>
                <p>No Messages Yet!</p>
            </div>
        )
    }

    //Compile
    return (
        <div>
            <h2>Hello {props.userName}</h2>
             <button onClick = {() => onButtonClicked("hello")}>Send Message</button>
             {chatDiv}
        </div>
    )
}
