//Dependencies//
import React from "react";
import { client, w3cwebsocket as W3CWebSocket } from "websocket";
import { useEffect, useState, useRef } from "react";

//Some bullshit from Ant Designs
import { Card, Avatar, Input, Typography } from "antd";
import "antd/dist/antd.css";
import "./index.css";

const { Search } = Input;
const { Text } = Typography;
const { Meta } = Card;

//Components//
import Home from "./Home";
import Login from "./Login";

//Define socket
const socketClient = new W3CWebSocket("ws://localhost:8000/");
export default function App() {
  //Dont' let anything happen until websocket connection is made
  let [isOpen, setIsOpen] = useState(false);

  //Authentication State
  let [userState, setUserState] = useState({
    userName: "",
    isLoggedIn: false,
  });

  let [chatHistory, setChatHistory] = useState([]);

  useEffect(() => {
    //Open up connection
    socketClient.onopen = () => {
      console.log("Websocket Client Connected");
      setIsOpen(true);
    };

    socketClient.onmessage = (message) => {
        console.log("chat history inside onmessage", chatHistory)
      const dataFromServer = JSON.parse(message.data);
      console.log("Reply from Server: ", dataFromServer);

      if (dataFromServer.type === "message") {
        let incomingMessage = {
          msg: dataFromServer.msg,
          user: dataFromServer.user,
        };

        updateMessages(incomingMessage);
      }
    };
  });

  function updateMessages(incomingMessage) {
      console.log("chathistory: ", chatHistory);
      console.log("chat history before adding")
      let chatbox = chatHistory
      console.log("chatbox, ", typeof(chatbox))
      let newMessage = chatbox.push(incomingMessage);
      setChatHistory(newMessage);
      console.log("chathistory: ", chatHistory);
      
  }

  //<--- Conditionally Render Components--->//
  let loginComponent;
  userState.isLoggedIn === false
    ? // loginComponent = <Login />
      (loginComponent = (
        <div>
          <Search
            placeholder="Enter Username"
            enterButton="Go Forth"
            size="large"
            onSearch={(value) =>
              setUserState({ userName: value, isLoggedIn: true, messages: [] })
            }
          />
        </div>
      ))
    : (loginComponent = (
        <Home userName={userState.userName} isSocketOpen={isOpen} />
      ));
  return (
    <div>
      <h4>Home</h4>
      {loginComponent}
    </div>
  );
}
